CREATE TABLE users (
	userid INTEGER NOT NULL, 
	username VARCHAR(100) NOT NULL, 
	email VARCHAR(100) NOT NULL, 
	password_hash VARCHAR(54) NOT NULL, 
	reg_date DATETIME NOT NULL, 
	avatar VARCHAR(256) NOT NULL, 
	balance INTEGER NOT NULL, 
	PRIMARY KEY (userid), 
	UNIQUE (username), 
	UNIQUE (email)
);
