CREATE TABLE category (
	categoryid INTEGER NOT NULL, 
	name VARCHAR(100) NOT NULL, 
	description VARCHAR(1000) NOT NULL, 
	material_icon_id VARCHAR(64) NOT NULL, 
	PRIMARY KEY (categoryid), 
	UNIQUE (name)
);
