INSERT INTO projects VALUES(8,3,2,'Drone','/static/images/category/3-project-default.jpg','Wanted a drone this summer?? A new drone awaits you',replace(replace('A drone is a flying device capable of doing tasks with minimal computation on air.\r\nSo what this drone does??\r\nPowered with AI, this drone flies and takes photos only when it is clear of the shot ie only when it know it is good photo..','\r',char(13)),'\n',char(10)),'The US airspace',10000000,0,'Drone, Design and Technology, life, Selfie, Photos','2018-05-01 22:37:38.883323');
INSERT INTO projects VALUES(9,1,2,'Avengers : Infinity War','/static/images/category/1-project-default.jpg','A realistic VR enabled 3D game of the latest avengers movie...',replace(replace('Wanna play as Iron Man or Captain America?\r\nExperience the avenger world in this fantastic avengers game..','\r',char(13)),'\n',char(10)),'USA',1000000,0,'Avengers, Infinity War, VR, 3D','2018-05-01 22:40:25.594831');
INSERT INTO projects VALUES(10,1,2,'RoadRash(Updated Graphics)','/static/images/category/1-project-default.jpg','Wait is finally over!! RoadRash with updated graphics!!',replace(replace('Couldn''t find the classic RoadRash game??\r\nThis arcade racing game is an evolution to the old RoadRash game..\r\nKick Cops, Race, Use nitro.. and what not','\r',char(13)),'\n',char(10)),'World',100000,0,'Graphics, Games, RoadRash, Arcade, Action, Classic, Retro, Racing','2018-05-01 22:44:19.139963');
